import{_ as l,c as a,o as e,a2 as i}from"./chunks/framework.DU0iKRkB.js";const u=JSON.parse('{"title":"第一阶段：数学基础补强","description":"掌握线性代数、微积分、概率论与统计的核心知识","frontmatter":{"title":"第一阶段：数学基础补强","description":"掌握线性代数、微积分、概率论与统计的核心知识","order":1},"headers":[],"relativePath":"guide/01-math-basics/index.md","filePath":"guide/01-math-basics/index.md","lastUpdated":1768553241000}'),r={name:"guide/01-math-basics/index.md"};function o(s,t,n,d,h,g){return e(),a("div",null,[...t[0]||(t[0]=[i('<h1 id="第一阶段-数学基础补强" tabindex="-1">第一阶段：数学基础补强 <a class="header-anchor" href="#第一阶段-数学基础补强" aria-label="Permalink to &quot;第一阶段：数学基础补强&quot;">​</a></h1><h2 id="阶段目标" tabindex="-1">阶段目标 <a class="header-anchor" href="#阶段目标" aria-label="Permalink to &quot;阶段目标&quot;">​</a></h2><p>掌握AI/机器学习所需的核心数学知识，为后续学习打下坚实基础。重点不是成为数学家，而是建立直觉，理解公式背后的几何意义和物理意义，并能用代码实现。</p><h2 id="学习时间规划" tabindex="-1">学习时间规划 <a class="header-anchor" href="#学习时间规划" aria-label="Permalink to &quot;学习时间规划&quot;">​</a></h2><ul><li><strong>总时长</strong>： 2-3个月</li><li><strong>每周投入</strong>： 15-20小时</li><li><strong>建议节奏</strong>： 每个子模块2-3周</li></ul><h2 id="核心模块导航" tabindex="-1">核心模块导航 <a class="header-anchor" href="#核心模块导航" aria-label="Permalink to &quot;核心模块导航&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">学习建议</p><p>数学是AI的基石。遇到不懂的概念，先寻找直观的几何解释（如3Blue1Brown的视频），再看公式推导，最后通过代码实现来加深理解。</p></div><table tabindex="0"><thead><tr><th style="text-align:left;">模块</th><th style="text-align:left;">内容重点</th><th style="text-align:left;">预计时长</th></tr></thead><tbody><tr><td style="text-align:left;"><strong><a href="./01-linear-algebra.html">01. 线性代数</a></strong></td><td style="text-align:left;">向量、矩阵运算、特征值、SVD分解</td><td style="text-align:left;">3-4周</td></tr><tr><td style="text-align:left;"><strong><a href="./02-calculus.html">02. 微积分</a></strong></td><td style="text-align:left;">导数、梯度、链式法则、梯度下降</td><td style="text-align:left;">3-4周</td></tr><tr><td style="text-align:left;"><strong><a href="./03-probability-statistics.html">03. 概率统计</a></strong></td><td style="text-align:left;">概率分布、贝叶斯定理、极大似然估计、信息论</td><td style="text-align:left;">3-4周</td></tr><tr><td style="text-align:left;"><strong><a href="./04-projects.html">04. 综合项目</a></strong></td><td style="text-align:left;">线性回归实现、高斯混合模型</td><td style="text-align:left;">1-2周</td></tr></tbody></table><h2 id="阶段验收与总结" tabindex="-1">阶段验收与总结 <a class="header-anchor" href="#阶段验收与总结" aria-label="Permalink to &quot;阶段验收与总结&quot;">​</a></h2><h3 id="验收标准" tabindex="-1">验收标准 <a class="header-anchor" href="#验收标准" aria-label="Permalink to &quot;验收标准&quot;">​</a></h3><h4 id="_1-理论理解-笔试自测" tabindex="-1">1. 理论理解 (笔试自测) <a class="header-anchor" href="#_1-理论理解-笔试自测" aria-label="Permalink to &quot;1. 理论理解 (笔试自测)&quot;">​</a></h4><ul><li><strong>线性代数</strong>：能够手动计算简单的矩阵乘法、特征值；理解矩阵变换的几何意义。</li><li><strong>微积分</strong>：能够计算多元函数的偏导数和梯度；能够推导梯度下降公式。</li><li><strong>概率统计</strong>：能够应用贝叶斯定理解决实际概率问题；理解假设检验的基本流程。</li></ul><h4 id="_2-编程能力-代码实战" tabindex="-1">2. 编程能力 (代码实战) <a class="header-anchor" href="#_2-编程能力-代码实战" aria-label="Permalink to &quot;2. 编程能力 (代码实战)&quot;">​</a></h4><ul><li><strong>NumPy基础</strong>：熟练使用NumPy进行向量化计算，避免使用循环。</li><li><strong>算法实现</strong>：能够从零实现梯度下降算法优化简单函数。</li><li><strong>数据思维</strong>：能够用统计量描述数据特征，并发现数据中的异常。</li></ul><h3 id="自我评估清单" tabindex="-1">自我评估清单 <a class="header-anchor" href="#自我评估清单" aria-label="Permalink to &quot;自我评估清单&quot;">​</a></h3><ul><li>[ ] 理解标量、向量、矩阵的定义和区别</li><li>[ ] 熟练运用基本导数公式和链式法则</li><li>[ ] 掌握常用概率分布（正态分布等）</li><li>[ ] 理解梯度的几何意义及梯度下降原理</li><li>[ ] 掌握贝叶斯定理并能应用</li><li>[ ] 完成所有模块的基础练习和编程练习</li><li>[ ] 完成至少一个综合项目</li><li>[ ] 整理了属于自己的数学知识笔记</li></ul><h2 id="常见问题-q-a" tabindex="-1">常见问题 (Q&amp;A) <a class="header-anchor" href="#常见问题-q-a" aria-label="Permalink to &quot;常见问题 (Q&amp;A)&quot;">​</a></h2><p><strong>Q1: 数学基础要学到什么程度？</strong><strong>A</strong>: 理解概念和直觉比死记公式更重要。重点理解：</p><ol><li>为什么需要这个数学工具？</li><li>在AI中的什么场景使用？</li><li>如何用代码实现？</li></ol><p><strong>Q2: 如果某个概念不理解怎么办？</strong><strong>A</strong>:</p><ol><li>先看直观解释（推荐 3Blue1Brown, StatQuest）。</li><li>再看数学推导。</li><li>动手编程实现。</li><li>不要卡太久，可以标记后续回看。</li></ol><p><strong>Q3: 需要做完所有习题吗？</strong><strong>A</strong>: 不必全部完成，但每种类型至少做2-3道典型题，确保掌握解题思路。</p>',22)])])}const f=l(r,[["render",o]]);export{u as __pageData,f as default};
