import{_ as s,c as a,o as t,a2 as e}from"./chunks/framework.C2eRlmqf.js";const c=JSON.parse('{"title":"模块四：Jupyter Notebook最佳实践","description":"魔法命令、快捷键与高效工作流","frontmatter":{"title":"模块四：Jupyter Notebook最佳实践","description":"魔法命令、快捷键与高效工作流","order":4},"headers":[],"relativePath":"guide/02-python-data/04-jupyter.md","filePath":"guide/02-python-data/04-jupyter.md","lastUpdated":1768553241000}'),o={name:"guide/02-python-data/04-jupyter.md"};function l(n,i,d,p,h,r){return t(),a("div",null,[...i[0]||(i[0]=[e(`<h1 id="模块四-jupyter-notebook最佳实践" tabindex="-1">模块四：Jupyter Notebook最佳实践 <a class="header-anchor" href="#模块四-jupyter-notebook最佳实践" aria-label="Permalink to &quot;模块四：Jupyter Notebook最佳实践&quot;">​</a></h1><p>Jupyter Notebook 是数据科学家的标准IDE，它支持代码、Markdown文档、数学公式和可视化图表混合排版。</p><h2 id="_4-1-常用快捷键" tabindex="-1">4.1 常用快捷键 <a class="header-anchor" href="#_4-1-常用快捷键" aria-label="Permalink to &quot;4.1 常用快捷键&quot;">​</a></h2><p><strong>命令模式 (Esc)</strong>:</p><ul><li><code>A</code>: 在上方插入单元格</li><li><code>B</code>: 在下方插入单元格</li><li><code>D, D</code>: 删除当前单元格</li><li><code>M</code>: 切换为 Markdown 模式</li><li><code>Y</code>: 切换为 Code 模式</li><li><code>Shift + M</code>: 合并选中单元格</li></ul><p><strong>编辑模式 (Enter)</strong>:</p><ul><li><code>Shift + Enter</code>: 运行当前单元格并选中下一个</li><li><code>Ctrl + Enter</code>: 运行当前单元格</li><li><code>Tab</code>: 代码补全</li><li><code>Shift + Tab</code>: 查看函数文档（非常有用！）</li></ul><h2 id="_4-2-魔法命令-magic-commands" tabindex="-1">4.2 魔法命令 (Magic Commands) <a class="header-anchor" href="#_4-2-魔法命令-magic-commands" aria-label="Permalink to &quot;4.2 魔法命令 (Magic Commands)&quot;">​</a></h2><p>以 <code>%</code> 开头的特殊命令。</p><ul><li><code>%timeit</code>: 测量代码运行时间（多次运行取平均）。<div class="language-python vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">%</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">timeit [x</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">**</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">2</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> for</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> x </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">in</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> range</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">1000</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)]</span></span></code></pre></div></li><li><code>%time</code>: 测量单次运行时间。</li><li><code>%pwd</code>: 显示当前工作目录。</li><li><code>%ls</code>: 列出文件。</li><li><code>%matplotlib inline</code>: 确保图表在Notebook中显示（现在的版本通常默认支持，但加上保险）。</li><li><code>%load_ext autoreload</code>: 自动重载外部模块，开发库时非常有用。<div class="language-python vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">%</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">load_ext autoreload</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">%</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">autoreload </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">2</span></span></code></pre></div></li></ul><h2 id="_4-3-notebook-结构规范" tabindex="-1">4.3 Notebook 结构规范 <a class="header-anchor" href="#_4-3-notebook-结构规范" aria-label="Permalink to &quot;4.3 Notebook 结构规范&quot;">​</a></h2><p>一个优秀的分析 Notebook 应该像一篇文章一样清晰：</p><ol><li><strong>标题与元数据</strong>：项目名称、作者、日期。</li><li><strong>导入库</strong>：所有 <code>import</code> 放在第一个单元格。</li><li><strong>配置</strong>：设置绘图风格、pandas显示选项等。<div class="language-python vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">pd.set_option(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;display.max_columns&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">None</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">plt.style.use(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;seaborn&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div></li><li><strong>数据加载</strong>：读取数据并展示 <code>head()</code>。</li><li><strong>数据清洗与探索</strong>：包含必要的 Markdown 注释，解释每一步的意图。</li><li><strong>分析与可视化</strong>：每个图表前应有分析假设，图表后应有结论洞察。</li><li><strong>总结</strong>：总结核心发现。</li></ol><h2 id="_4-4-导出与分享" tabindex="-1">4.4 导出与分享 <a class="header-anchor" href="#_4-4-导出与分享" aria-label="Permalink to &quot;4.4 导出与分享&quot;">​</a></h2><ul><li><strong>导出为 HTML/PDF</strong>：方便非技术人员阅读。</li><li><strong>清理 Output</strong>：在提交到 Git 之前，建议 <code>Kernel -&gt; Restart &amp; Clear Output</code>，以减小文件体积并避免冲突。</li></ul>`,15)])])}const g=s(o,[["render",l]]);export{c as __pageData,g as default};
