import{_ as s,c as a,o as t,a2 as n}from"./chunks/framework.C2eRlmqf.js";const g=JSON.parse('{"title":"模块四：循环神经网络 (RNN)","description":"序列数据处理与LSTM/GRU","frontmatter":{"title":"模块四：循环神经网络 (RNN)","description":"序列数据处理与LSTM/GRU","order":4},"headers":[],"relativePath":"guide/04-deep-learning/04-rnn.md","filePath":"guide/04-deep-learning/04-rnn.md","lastUpdated":1768553241000}'),e={name:"guide/04-deep-learning/04-rnn.md"};function l(r,i,h,o,p,d){return t(),a("div",null,[...i[0]||(i[0]=[n('<h1 id="模块四-循环神经网络-rnn" tabindex="-1">模块四：循环神经网络 (RNN) <a class="header-anchor" href="#模块四-循环神经网络-rnn" aria-label="Permalink to &quot;模块四：循环神经网络 (RNN)&quot;">​</a></h1><p>用于处理序列数据（文本、时间序列、语音）。</p><h2 id="_4-1-基础-rnn" tabindex="-1">4.1 基础 RNN <a class="header-anchor" href="#_4-1-基础-rnn" aria-label="Permalink to &quot;4.1 基础 RNN&quot;">​</a></h2><ul><li><strong>原理</strong>: 当前时刻的输出依赖于当前输入和上一时刻的隐藏状态。</li><li><strong>问题</strong>: 梯度消失/爆炸，无法捕捉长距离依赖。</li></ul><h2 id="_4-2-lstm-长短期记忆网络-⭐⭐⭐⭐⭐" tabindex="-1">4.2 LSTM (长短期记忆网络) ⭐⭐⭐⭐⭐ <a class="header-anchor" href="#_4-2-lstm-长短期记忆网络-⭐⭐⭐⭐⭐" aria-label="Permalink to &quot;4.2 LSTM (长短期记忆网络) ⭐⭐⭐⭐⭐&quot;">​</a></h2><p>通过引入 <strong>门控机制 (Gating)</strong> 解决了长距离依赖问题。</p><ul><li><strong>遗忘门 (Forget Gate)</strong>: 决定丢弃什么信息。</li><li><strong>输入门 (Input Gate)</strong>: 决定更新什么信息。</li><li><strong>输出门 (Output Gate)</strong>: 决定输出什么信息。</li><li><strong>细胞状态 (Cell State)</strong>: 贯穿整个序列的“高速公路”。</li></ul><div class="language-python vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">nn.LSTM(</span><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">input_size</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">10</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">hidden_size</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">20</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">num_layers</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">2</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#E36209;--shiki-dark:#FFAB70;">batch_first</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">True</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div><h2 id="_4-3-gru-门控循环单元" tabindex="-1">4.3 GRU (门控循环单元) <a class="header-anchor" href="#_4-3-gru-门控循环单元" aria-label="Permalink to &quot;4.3 GRU (门控循环单元)&quot;">​</a></h2><p>LSTM 的简化版，只有重置门和更新门，参数更少，训练更快，效果通常相当。</p><h2 id="_4-4-应用场景" tabindex="-1">4.4 应用场景 <a class="header-anchor" href="#_4-4-应用场景" aria-label="Permalink to &quot;4.4 应用场景&quot;">​</a></h2><ul><li><strong>文本分类</strong>: 情感分析。</li><li><strong>序列标注</strong>: 命名实体识别。</li><li><strong>机器翻译</strong>: Seq2Seq 模型。</li><li><strong>时间序列预测</strong>: 股票、天气。</li></ul>',12)])])}const u=s(e,[["render",l]]);export{g as __pageData,u as default};
