import{_ as r,c as a,o,a2 as t}from"./chunks/framework.DU0iKRkB.js";const p=JSON.parse('{"title":"综合项目：深度学习实战","description":"图像分类与文本情感分析","frontmatter":{"title":"综合项目：深度学习实战","description":"图像分类与文本情感分析","order":6},"headers":[],"relativePath":"guide/04-deep-learning/06-projects.md","filePath":"guide/04-deep-learning/06-projects.md","lastUpdated":1768553241000}'),i={name:"guide/04-deep-learning/06-projects.md"};function n(l,e,s,d,c,h){return o(),a("div",null,[...e[0]||(e[0]=[t('<h1 id="综合项目-深度学习实战" tabindex="-1">综合项目：深度学习实战 <a class="header-anchor" href="#综合项目-深度学习实战" aria-label="Permalink to &quot;综合项目：深度学习实战&quot;">​</a></h1><h2 id="项目一-cifar-10-图像分类" tabindex="-1">项目一：CIFAR-10 图像分类 <a class="header-anchor" href="#项目一-cifar-10-图像分类" aria-label="Permalink to &quot;项目一：CIFAR-10 图像分类&quot;">​</a></h2><p><strong>目标</strong>：从零构建一个 ResNet 模型（或使用预训练模型），在 CIFAR-10 数据集上达到 90%+ 的准确率。</p><h3 id="关键步骤" tabindex="-1">关键步骤 <a class="header-anchor" href="#关键步骤" aria-label="Permalink to &quot;关键步骤&quot;">​</a></h3><ol><li><strong>数据增强</strong>: RandomCrop, RandomHorizontalFlip, Normalize。</li><li><strong>模型构建</strong>: 使用 <code>torchvision.models.resnet18</code>。</li><li><strong>训练策略</strong>: <ul><li>Optimizer: SGD with Momentum。</li><li>Scheduler: CosineAnnealingLR。</li><li>Loss: CrossEntropyLoss。</li></ul></li><li><strong>可视化</strong>: 使用 TensorBoard 记录 Loss 和 Accuracy。</li></ol><h2 id="项目二-imdb-电影评论情感分析" tabindex="-1">项目二：IMDB 电影评论情感分析 <a class="header-anchor" href="#项目二-imdb-电影评论情感分析" aria-label="Permalink to &quot;项目二：IMDB 电影评论情感分析&quot;">​</a></h2><p><strong>目标</strong>：使用 LSTM 或 Transformer 对影评进行正负面分类。</p><h3 id="关键步骤-1" tabindex="-1">关键步骤 <a class="header-anchor" href="#关键步骤-1" aria-label="Permalink to &quot;关键步骤&quot;">​</a></h3><ol><li><strong>文本预处理</strong>: Tokenization, Padding, 构建词表。</li><li><strong>Embedding</strong>: 使用 <code>nn.Embedding</code> 或预训练的 GloVe 词向量。</li><li><strong>模型构建</strong>: <ul><li>Bi-LSTM + Linear。</li><li>或 Fine-tune BERT。</li></ul></li><li><strong>评估</strong>: Accuracy, F1-Score。</li></ol><h2 id="进阶挑战" tabindex="-1">进阶挑战 <a class="header-anchor" href="#进阶挑战" aria-label="Permalink to &quot;进阶挑战&quot;">​</a></h2><p>尝试实现 <strong>Style Transfer (风格迁移)</strong>，将一张图片的风格迁移到另一张图片上。这需要理解 CNN 的特征图如何表示内容和风格。</p>',11)])])}const u=r(i,[["render",n]]);export{p as __pageData,u as default};
